import React from 'react';
import './Visualizer.css';
import * as algorithms from'.Algorithms/algorithms.js';


export default class Visualizer extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
            array: [],
        };
    }
    componentDidMount(){
        this.resetArray();
    }
    resetArray(){
        const array = [];
        for (let i = 0; i<100; i++){
            array.push (randomIntFromInterval(20,650));
        }
        this.setState({array});
    }
    mergeSort() {
        const javaScriptSortedArray = this.state.array.slice().sort((a, b) => a - b);
        const sortedArray = mergeSort (this.state.array);
        console.log(arraysAreEqual(javaScriptSortedArray, sortedArray)); 
    }

    quickSort() {}

    heapSort() {}

    bubbleSort() {}

    testAlgorithms(){
        for (let i = 0; i<100; i++){
            const array = [];
            const lenth = randomIntFromInterval(1,1000);
            for (let i = 0; i< length; i++) {
                array.push(randomIntFromInterval(-1000,1000));
            }
            const javaScriptSortedArray = array.slice().sort((a, b) => a - b);
            const mergeSortArray = sortingAlgorithms.mergeSort(array.slice());
            console.log(arraysAreEqual(javaScriptSortedArray,mergeSortArray))
        }
    }

    render(){
        const {array} = this.state;

        return (
            <div className="array-container">
            {array.map((value,idx) => (
                <div className="array-bar" key={idx}
                style={{height: `${value}px`}}>
                </div>
            ))} <br></br><br></br>
            <button class="button-a" onClick={() => this.resetArray()} >Create New Array</button> 
            <button class="button-a" onClick={() => this.mergeSort()} >Merge Sort</button> 
            <button class="button-a" onClick={() => this.quickSort()} >Quick Sort</button>  
            <button class="button-a" onClick={() => this.heapSort()} >Heap Sort</button>  
            <button class="button-a" onClick={() => this.bubbleSort()} >Bubble Sort</button>                                                 
            </div>
        );
    }
}
//From https:stackoverflow.com/questions/4959975/generate-random-number-between-two-numbers-
function randomIntFromInterval(min, max) {
    return Math.floor (Math.random() * (max - min + 1) + min);
}


function arraysAreEqual (arrayOne, arrayTwo) {
    if (arrayOne.length !== arrayTwo.length) return false;
    for (let i= 0; i<arrayOne.length; i++) {
        if (arrayOne [i] !== arraytwo[i]) return false;
    }
    return true;
}
