import React, { useState, useEffect } from 'react';
import { Link, navigate } from '@reach/router';
import axios from 'axios';

const FormPirate = props => {
    const { action, update } = props;
    const [newErrors, setNewErrors] = useState([]);
    const [data, setData] = useState([]);
    const [pirate, setPirate] = useState([]);
    const [user, setUser] = useState([]);
    const [newPirate, setNewPirate] = useState({
        name: "",
        image: "",
        chests: "",
        position: "",
        peg: "",
        patch: "",
        hook: ""
    });

    const [errors, setErrors] = useState({
        name: "",
        image: "",
        chests: "",
        position: "",
        peg: "",
        patch: "",
        hook: ""
    })


    const [hasBeenSubmitted, setHasBeenSubmitted] = useState(false);

    const formMessage = () => {
        if( hasBeenSubmitted ) {
            return "Thank you for adding a new pirate to our crew!";
        } else {
            return "";
        }
    };

    useEffect(() => {
        if(action === "update"){
            axios.put(`http://localhost:8000/api/users/${props.id}/update`)
                .then(response => {
                    if(response.data.message === "Success!")
                        setNewPirate(response.data.results)
                    else
                        navigate("/users/display/all");
                })
        }
    },[])

    const submitHandler = e => {
        e.preventDefault();
        if(validate(newPirate)){
            if(action === "update"){
                axios.put(`http://localhost:8000/api/users/${props.id}`, newPirate/update)
                    .then(response => {
                        if(response.data.message === "Success!"){
                            navigate("/users/display/all");
                        }
                    })
                    .catch(err => console.log(err));
            } 
        }
    }

    const changeHandler = e => {
        const curPirate = {
            ...newPirate,
            [e.target.name]: e.target.value
        }
        validate(curPirate);
        setNewPirate(curPirate);
        setHasBeenSubmitted( true );
    }

    const validate = user => {
        let valid = false;
        const {...curErrors} = errors;
        if(user.name.length === 0){
            curErrors.name = "The pirate will need a name. This is a required field.";
        } else if(user.name.length < 2) {
            curErrors.name = "First name must be at least 2 characters in length.";
        } else {
            curErrors.name = "";
            valid = true;
        }

        if(user.image.length === 0){
            curErrors.image = "An image url is required.";
        } else {
            curErrors.image = "";
            valid = true;
        }

        if(user.chests.length === 0){
            curErrors.chests = "The amount of your riches is a required field.";
        } else {
            curErrors.chests = "";
            valid = true;
        }

        if(user.position.length === 0){
            curErrors.position = "Your role on this crew is a reguired field.";
        } else if(user.position === user.position.captain) {
            curErrors.position = "Only one captain is allowed per crew and we already have one.";
        } else {
            curErrors.position = "";
            valid = true;
        }

        if(user.peg.length === 0){
            curErrors.peg = "This is a required field.";
        } else {
            curErrors.peg = "";
            valid = true;
        }
        if(user.patch.length === 0){
            curErrors.patch = "This is a required field.";
        } else {
            curErrors.patch = "";
            valid = true;
        }

        if(user.hook.length === 0){
            curErrors.hook = "This is a required field.";
        } else {
            curErrors.hook = "";
            valid = true;
        }



        setErrors(curErrors);

        return valid;
    }

    const deleteHandler = () => {
        axios.delete(`http://localhost:8000/api/users/${user.id}/${props.id}`)
            .then(response => {
                if(response.data.message === "Success!"){
                    navigate("/users/display/all")
                }
            })
            .catch(err => console.log(err));
    }

    return (
        <div>
            {/* Create Pirate Form */}
            <div className="container" style={{border:"5px solid black", backgroundColor:"orange", maxWidth:"40%", display:"inline-block", margin:"5% 50% 0% 1%"}}> 
                <div className="container" style={{textAlign:"center", width:"100%", padding:"0px 0px 0px 0px", backgroundColor:"#8b572a", color:"white", borderBottom:"2px solid black", margin:"0% 0% 0% 0%"}}>
                    <h1 style={{margin:"10% 0% 10% 55%"}}>Add Pirate</h1>
                </div>
                <form onSubmit={ submitHandler } >
                    <div>
                        <div className="form-group col-xs-6">
                                {
                                    errors.name ? 
                                    <p className="col-sm-6 text-left text-nowrap text-danger">{ errors.name }</p>
                                    :
                                    ''
                                }
                            <label htmlFor="name" style={{margin:"0% 80% 0% 0%"}}>Name: </label>
                            <input 
                                type="text" 
                                name="name" 
                                className="form-control"
                                onChange={ changeHandler }
                                value={ newPirate.name }
                            />
                        </div>

                        <div className="form-group col-xs-6">
                            {
                                errors.image ? 
                                <p className="col-sm-6  text-sm-left text-danger">{ errors.lastName }</p>
                                :
                                ''
                            }
                            <label htmlFor="image" style={{margin:"0% 80% 0% 0%"}}>Image: </label>
                            <input 
                                type="text" 
                                name="image" 
                                className="form-control"
                                onChange={ changeHandler }
                                value={ newPirate.image }
                            />
                        </div>

                        <div className="form-group col-xs-6">
                            {
                                errors.chests ? 
                                <p className="col-sm-6 text-nowrap text-danger">{ errors.chests }</p>
                                :
                                ''
                            }
                            <label htmlFor="chests" style={{margin:"0% 90% 0% 0%"}}>Chests: </label>
                            <input 
                                type="text" 
                                name="chests" 
                                className="form-control "
                                onChange={ changeHandler }
                                value={ newPirate.chests }
                            />
                        </div>              

                        <div className="form-group col-xs-6">
                            {
                                errors.position ? 
                                <p className="col-sm-6 text-left text-danger">{ errors.position }</p>
                                :
                                ''
                            }
                            <label htmlFor="position" style={{margin:"0% 90% 0% 0%"}}>Position: </label>
                            <input 
                                type="text" 
                                name="position" 
                                className="form-control"
                                onChange={ changeHandler }
                                value={ newPirate.position }
                            />
                        </div>

                        <div className="form-check col-xs-6">
                            {/* {
                                errors.peg ? 
                                <p className="col-sm-8 text-left text-danger">{ errors.peg }</p>
                                :
                                ''
                            } */}
                            <label className="form-check-label" htmlFor="peg" style={{margin:"0% 10% 0% 35%"}}>Peg Leg: </label>
                            <input 
                                type="checkbox" 
                                name="peg" 
                                className="form-check-input"
                                onChange={ changeHandler }
                                value={ newPirate.peg }
                            />
                        </div>
                        <div className="form-group col-xs-6">
                            {/* {
                                errors.patch ? 
                                <p className="col-sm-8 text-left text-danger">{ errors.patch }</p>
                                :
                                ''
                            }  */}
                            <label className="form-check-label" htmlFor="patch" style={{margin:"0% 10% 0% 35%"}}>Eye Patch: </label>
                            <input 
                                type="checkbox" 
                                name="patch" 
                                className="form-check-input"
                                onChange={ changeHandler }
                                value={ newPirate.patch }
                            />
                        </div>
                        <div className="form-group col-xs-6">
                            {/* {
                                errors.hook ? 
                                <p className="col-sm-8 text-left text-danger">{ errors.hook }</p>
                                :
                                ''
                            } */}
                            <label className="form-check-label" htmlFor="hook" style={{margin:"0% 10% 0% 35%"}}>Hook Hand: </label>
                            <input 
                                type="checkbox" 
                                onClick="true"
                                name="hook" 
                                className="form-check-input"
                                onChange={ changeHandler }
                                value={ newPirate.hook }
                            />
                        </div>
                        <div className="form-group col-xs-6">
                            <input type="submit" value="Update" action="update" className="col-md-6 btn btn-primary" style={{margin:"0% 0% 0% 45%"}}/><br></br>
                            <p>{ formMessage() }</p>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default FormPirate;
